// Hide Header on on scroll down
var didScroll;
var lastScrollTop = 0;
var delta = 5;
//var navbarHeight = jQuery('#shiftnav-toggle-main').outerHeight();
var navbarHeight = 70;
jQuery(window).scroll(function(event){
    didScroll = true;
});
setInterval(function() {
    if (didScroll) {
        hasScrolled();
        didScroll = false;
    }
}, 250);
function hasScrolled() {
    var st = jQuery(this).scrollTop();
    // Make sure they scroll more than delta
    if(Math.abs(lastScrollTop - st) <= delta)
        return;
	// If they scrolled down and are past the navbar, add class .nav-up.
    // This is necessary so you never see what is "behind" the navbar.
 	if (st > lastScrollTop && st > navbarHeight){
        // Scroll Down
        jQuery('#shiftnav-toggle-main').addClass('nav-up');
    } else {
        // Scroll Up
        if(st + jQuery(window).height() < jQuery(document).height()) {
            jQuery('#shiftnav-toggle-main').removeClass('nav-up');
        }
    }
    lastScrollTop = st;
}
/*Close Hide Header on on scroll down*/
function init() {
	window.addEventListener('scroll', function(e){
		var distanceY = window.pageYOffset || document.documentElement.scrollTop,
		shrinkOn = 197,
		header = document.querySelector(".woocommerce-sticky-header");
		if (distanceY > shrinkOn) {
			//classie.add(header,"smaller");
			jQuery(header).addClass('smaller');
		} else {
			//if (classie.has(header,"smaller")) {
			if (jQuery(header).hasClass('smaller')) {
				//classie.remove(header,"smaller");
				jQuery(header).removeClass('smaller');
			}
		}
	});
}
window.onload = init();
